import React, { useEffect, useState } from 'react';
import { connectWebSocket, sendMessage } from '../services/websocket';

function Board() {
  const [gameState, setGameState] = useState(null);

  useEffect(() => {
    const socket = connectWebSocket('ws://localhost:3000');

    // Join the game
    sendMessage({ type: 'join', playerId: 'player1' });

    // Listen for game state updates
    const handleMessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        if (data.type === 'gameState') {
          setGameState(data.data);
        }
      } catch (error) {
        console.error('Error parsing message:', error);
      }
    };

    socket.addEventListener('message', handleMessage);

    return () => {
      socket.removeEventListener('message', handleMessage);
      socket.close(); // Clean up the WebSocket connection when the component unmounts
    };
  }, []);

  // Render the game board
  return (
    <div className="board">
      {gameState && gameState.board.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((cell, cellIndex) => (
            <div key={cellIndex} className="cell">
              {cell ? cell.type : ''}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}

export default Board;
